function composite_signal=calculate_composite_signal(original_signal,Fs,gain)
composite_signal= filter(fir_equiripple_0_170(Fs),original_signal)*gain(1)+filter(fir_equiripple_170_310(Fs),original_signal)*gain(2)+filter(fir_equiripple_310_600(Fs),original_signal)*gain(3)+filter(fir_equiripple_600_1000(Fs),original_signal)*gain(4)+filter(fir_equiripple_1000_3000(Fs),original_signal)*gain(5)+filter(fir_equiripple_3000_6000(Fs),original_signal)*gain(6)+filter(fir_equiripple_6000_12000(Fs),original_signal)*gain(7)+filter(fir_equiripple_12000_14000(Fs),original_signal)*gain(8)+filter(fir_equiripple_14000_16000(Fs),original_signal)*gain(9);